-- Create "attestation_policies" table
CREATE TABLE `attestation_policies` (`id` bigint NOT NULL AUTO_INCREMENT, `name` varchar(255) NOT NULL, `statement_policies` bigint NULL, PRIMARY KEY (`id`), INDEX `attestation_policies_statements_policies` (`statement_policies`), INDEX `attestationpolicy_name` (`name`), CONSTRAINT `attestation_policies_statements_policies` FOREIGN KEY (`statement_policies`) REFERENCES `statements` (`id`) ON UPDATE NO ACTION ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
-- Create "subject_scopes" table
CREATE TABLE `subject_scopes` (`id` bigint NOT NULL AUTO_INCREMENT, `subject` varchar(255) NOT NULL, `scope` varchar(255) NOT NULL, `attestation_policy_subject_scopes` bigint NULL, PRIMARY KEY (`id`), INDEX `subject_scopes_attestation_policies_subject_scopes` (`attestation_policy_subject_scopes`), INDEX `subjectscope_subject` (`subject`), CONSTRAINT `subject_scopes_attestation_policies_subject_scopes` FOREIGN KEY (`attestation_policy_subject_scopes`) REFERENCES `attestation_policies` (`id`) ON UPDATE NO ACTION ON DELETE SET NULL) CHARSET utf8mb4 COLLATE utf8mb4_bin;
